import './FeaturesTeamCard.scss';
import { Link } from "react-router-dom";
import React, { Component, Fragment } from 'react';
const classNames = require('classnames');

export default class FeaturesTeam extends Component {
  constructor() {
    super();
    this.state = {
      featuresTeam: []
    };
  }

  componentDidMount() {
    this.callApi()
      .then(featuresTeam => {
        this.setState({ featuresTeam });
      })
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const featuresTeam = await fetch('/api/v1/campaign/featuresTeam');
    const body = await featuresTeam.json();
    
    if (featuresTeam.status !== 200) throw Error(body.message);
    
    return body;
  };

  render() {
    return (
      <Fragment>

        {this.state.featuresTeam.map(ft => (     
          <Link
            key={ft.id}
            to={{ pathname: `/Section/${ft.name}` }}
          >
            <div className="ft-card" key={ft.id}>
                <div className={classNames('ft-card-icon', ft.theme)}>
                  <i className={ft.icon} />
                </div>
                <h3>{ft.name}</h3>
                <p>Pas de campagne</p>
                <div className="hr"></div>
                <div className="ft-card-statut"></div>
                <p>...</p>
            </div>
          </Link>
        ))}

      </Fragment>
    )
  }
}
