import React, { Component } from "react";
import "./App.scss";
import FeaturesTeam from "./components/FeaturesTeam/FeaturesTeamCard/FeaturesTeamCard";
import Header from "./components/Header/Header";
import Form from "./components/Form/Form";
import ContactSection from "./components/FeaturesTeam/FeaturesTeamSection/FeaturesTeamSection";

import { BrowserRouter as Router, Route } from "react-router-dom";

class App extends Component {
  constructor() {
    super();
    this.state = {
      featuresTeam: [],
      ft_id: []
    };
  }

  componentDidMount() {
    sessionStorage.clear();
    this.callApi()
      .then(featuresTeam => {
        this.setState({ featuresTeam });
      })
      .then(ft_id => {
        this.setState({ ft_id });
        // this.setState({ ft_id: res.tableau });
        sessionStorage.setItem("ft-id", this.props.match.params.teamId);
        // sessionStorage.setItem("ft_id", res.tableau[0].ft_id);
        this.Id = sessionStorage.getItem("ft-id");
        // console.log(res.tableau[0].ft_id);
      })

      .catch(err => console.log(err));
  }

  callApi = async () => {
    const featuresTeam = await fetch("/api/v1/campaign/getfeaturesTeam");
    const body = await featuresTeam.json();

    if (featuresTeam.status !== 200) throw Error(body.message);

    return body;
  };

  render() {
    return (
      <Router>
        <div className="App">
          <Header />

          <Route
            exact
            path="/"
            render={props => (
              <div className="ftWrapper">
                <FeaturesTeam />
              </div>
            )}
          />
          <Route path="/Form" component={Form} />
          <Route path="/Section/:teamName" component={ContactSection} />
          {/* <Route path="/FeaturesTeamCampaign" component={FeaturesTeamCampaign} />  */}
        </div>
      </Router>
    );
  }
}

export default App;
