import React, { Component } from "react";
import "./App.scss";
import FeaturesTeam from "./components/FeaturesTeam/FeaturesTeam";
import FeaturesTeamCampaign from "./components/FeaturesTeam/FeaturesTeamCampaign/FeaturesTeamCampaign";
import Header from "./components/Header/Header";
import Form from "./components/Form/Form";
// import SearchSection from './components/FeaturesTeam/FeaturesTeamSection/Sections/SearchSection';
// import TestSection from './components/FeaturesTeam/FeaturesTeamSection/Sections/TestSection';
import ContactSection from "./components/FeaturesTeam/FeaturesTeamSection/FeaturesTeamSection";
// import PubliciteSection from './components/FeaturesTeam/FeaturesTeamSection/Sections/PubliciteSection';
// import AccelererSection from './components/FeaturesTeam/FeaturesTeamSection/Sections/AccelererSection';
// import AnnonceSection from './components/FeaturesTeam/FeaturesTeamSection/Sections/AnnonceSection';
// import IdentiteSection from './components/FeaturesTeam/FeaturesTeamSection/Sections/IdentiteSection';
// import PaiementSection from './components/FeaturesTeam/FeaturesTeamSection/Sections/PaiementSection';
// import ConcretisationSection from './components/FeaturesTeam/FeaturesTeamSection/Sections/ConcretisationSection';
// import ToolsSection from './components/FeaturesTeam/FeaturesTeamSection/Sections/ToolsSection';

import { BrowserRouter as Router, Route, Link } from "react-router-dom";

class App extends Component {
  constructor() {
    super();
    this.state = {
      featuresTeam: [],
      campagnes: [],
      response: []
    };
  }

  // componentDidMount() {
  //   this.callApi()
  //     .then(res => {
  //       this.setState({ response: res.results });
  //       sessionStorage.setItem("ft_id", res.results[0].ft_id);
  //       console.log(res.results[0].ft_id);
  //     })
  //     .catch(err => console.log(err));
  // }

  componentDidMount() {
    sessionStorage.clear();
    this.callApi()
      .then(featuresTeam => {
        this.setState({ featuresTeam });
      })
      .then(res => {
        this.setState({ response: res.tableau });
        sessionStorage.setItem("ft_id", res.tableau[0].ft_id);
        sessionStorage.getItem("ft_id");
        console.log(res.tableau[0].ft_id);
      })

      // .catch(err => console.log(err));

      .then(campagnes => {
        this.setState({ campagnes });
      })
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch("/api/v1/campaign/getFeaturesTeam");
    const body1 = await response.json();
    const featuresTeam = await fetch("/api/v1/campaign/getfeaturesTeam");
    const body = await featuresTeam.json();

    if (response.status !== 200) throw Error(body1.message);

    if (featuresTeam.status !== 200) throw Error(body.message);

    return body;
  };

  render() {
    return (
      <Router>
        <div className="App">
          <Header />

          <Route
            exact
            path="/"
            render={props => (
              <div className="ftWrapper">
                {this.state.featuresTeam.map(ft => (
                  <Link key={ft.id} to={{ pathname: `/section/${ft.name}` }}>
                    <FeaturesTeam
                      key={ft.id}
                      ftName={ft.name}
                      icon={ft.icon}
                      theme={ft.theme}
                      status="..."
                      campagneName="Pas de campagne"
                    />
                  </Link>
                ))}
              </div>
            )}
          />
          {/* <Route path="/section/Contact" component={ContactSection} />
          <Route path="/section/Publicité" component={PubliciteSection} />
          <Route path="/section/Accélérer" component={AccelererSection} />
          <Route path="/section/Annonce" component={AnnonceSection} /> 
          <Route path="/section/Identité" component={IdentiteSection} /> 
          <Route path="/section/Search" component={SearchSection} />
          <Route path="/section/Paiement" component={PaiementSection} />
          <Route path="/section/Concrétisation" component={ConcretisationSection} /> 
          <Route path="/section/Tools" component={ToolsSection} />                 
          <Route path="/section/Test" component={TestSection} />  */}
          <Route path="/Form" component={Form} />
          <Route path="/Section/:teamNom" component={ContactSection} />
          <Route
            path="/FeaturesTeamCampaign"
            component={FeaturesTeamCampaign}
          />
        </div>
      </Router>
    );
  }
}

export default App;
