import "./FeaturesTeamSection.scss";
import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom";
const classNames = require("classnames");

export default class FeaturesTeamSection extends Component {
  constructor(props) {
    super(props);
    sessionStorage.setItem("ft-Id", this.props.match.params.teamId);
    sessionStorage.setItem("ft-name", this.props.match.params.teamName);
    sessionStorage.setItem("ft-icon", this.props.match.params.teamIcon);
    this.Name = sessionStorage.getItem("ft-name");
    this.Icon = sessionStorage.getItem("ft-icon");
  }
  state = { show: false };

  showModal = () => {
    this.setState({ show: true });
  };

  hideModal = () => {
    this.setState({ show: false });
  };

  date = `Le ${new Date().toLocaleDateString()}`;

  render() {
    return (
      <Fragment>
        <div className={classNames("team-section-icon", this.props.theme)}>
          <i className={this.props.icon} />
        </div>
        <div className="team-section">
          <h1>Hello, {this.Name} !</h1>
          <div>
            <button type="button" onClick={this.showModal}>
              <i className="far fa-plus-square fa-5x" />
              Ajouter une campagne
            </button>
            <Link to="/FeaturesTeamCampaign">
              <button>
                <i className="far fa-eye" />
                Voir les campagnes
              </button>
            </Link>
          </div>
        </div>

        <Modal show={this.state.show} handleClose={this.hideModal}>
          <h1>Nouvelle Campagne</h1>
          <p dangerouslySetInnerHTML={{ __html: this.date }} />
          <img src={require("../../images/modelImg.png")} alt="popup" />
          <div>
            <input
              type="text"
              id="input"
              name=""
              placeholder="Nom de la campagne"
            />
            <Link to="/Form">
              <button className="modal-button" id="button-campagne">
                Commencer
              </button>
            </Link>
          </div>
        </Modal>
      </Fragment>
    );
  }
}

const Modal = ({ handleClose, show, children }) => {
  const showHideClassName = show ? "modal display-block" : "modal display-none";

  return (
    <div className={showHideClassName}>
      <div className="modal-main">
        <i className="fas fa-times fa-2x" onClick={handleClose} />
        {children}
      </div>
    </div>
  );
};
