import React, { Component, Fragment } from 'react';
// import Questions from '../Questions/Questions';
import { Link } from "react-router-dom";
const classNames = require('classnames');

export default class FeaturesTeamSection extends Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  state = { show: false }

  showModal = () => {
    this.setState({ show: true });
  }
  
  hideModal = () => {
    this.setState({ show: false });
  }

  date = `Le ${new Date().toLocaleDateString()}`;

  render() {
    return (
      <Fragment>
        <div className={classNames('team-section-icon', this.props.theme)}>
          <i className={this.props.icon}/>
        </div>
        <div className="team-section">
        <h1>Hello, {this.props.ftName} !</h1>
        <div>
          <button type="button" onClick={this.showModal}><i className="far fa-plus-square fa-5x"></i>Ajouter une campagne</button>
          <button><i className="far fa-eye"></i>Voir les campagnes</button>
        </div>              
        </div>
        
        <form onSubmit={this.handleSubmit}>
        <Modal show={this.state.show} handleClose={this.hideModal} >
              <h1>Nouvelle Campagne</h1>
              <p dangerouslySetInnerHTML={{ __html: this.date }}></p>
              <img src={require('../../images/modelImg.png')} alt="popup"/>
              <div>
                <input type="text" id="input" name="" placeholder="Nom de la campagne" value={this.state.value} onChange={this.handleChange}/>
                   <Fragment>
                    <Link to="/Form"> 
                     <button id="button-campagne" type="submit">Commencer</button>
                    </Link> 
                   </Fragment>
              </div> 
        </Modal>
        </form>
      </Fragment>
    )
  }
}

const Modal = ({ handleClose, show, children }) => {
  const showHideClassName = show ? 'modal display-block' : 'modal display-none';

  return (
    <div className={showHideClassName}>
      <div className='modal-main'>
        <i className="fas fa-times fa-2x" onClick={handleClose}></i>
        {children}
      </div>
    </div>
  );
};
